<div class="modal fade" id="seleccionarComprobante" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1"
    aria-labelledby="seleccionarComprobanteLabel" aria-hidden="true">
    <div class="modal-dialog modal-dialog-centered">
        <div class="modal-content">
            <div class="modal-header text-center">
                <h4 class="w-100 modal-title fs-5" id="staticBackdropLabel">Seleccionar Comprobante de Venta</h4>
            </div>
            <div class="modal-body">

                <div class="form-group">
                    <div class="input-group">
                        <div class="input-group-prepare">
                            <div class="input-group-text">
                                <i class="fas fa-print"></i>
                            </div>
                        </div>
                        <div id="select-comprobante" class="form-control"></div>
                    </div>
                </div>

            </div>
            <div class="modal-footer">
                <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Cerrar</button>
                <button type="submit" class="btn btn-primary seleccionar-comprobante" data-bs-dismiss="modal">Guardar Cambios</button>
            </div>
        </div>
    </div>
</div>
